  run_tests(tests);
  printf("num_assert: %lu\n", num_assert);
  return 0;
}

test_decode_chinese:
void test_decode_chinese() {
  const char *s = "成为更健康、更长久的世界一流企业";
  uint32_t res[64] = {};
  uint32_t codepoint;
  size_t count = 0;
  uint32_t state = 0;
  for (; *s; ++s) {
    if (!decode_utf8(&state, &codepoint, *s))
      res[count++] = codepoint;
  }
  assert(state == UTF8_ACCEPT);
  assert(count == 16);
  assert(res[1] == 0x4e3a);
  assert(res[2] == 0x66f4);
  assert(res[4] == 0x5eb7);
  assert(res[8] == 0x4e45);
}
